import React, { Component } from "react";

import { BrowserRouter, Route, Switch, Link } from "react-router-dom";
import { List, Write, View } from "./index.js";

import { Right_Write } from "./right/index.js";
import { Category } from "./left/index.js";
import { Button } from "react-bootstrap";

import axios from "axios";
import "./main.css";

class main extends Component {
  constructor(props) {
    super(props);
    this.state = {
      category: "",
      category_change: false,
      contents: "",
      title: "",
    };
  }

  _withProps = function (Component, props) {
    return function (matchProps) {
      return <Component {...props} {...matchProps} />;
    };
  };

  _changeState = () => {
    this.setState({ category_change: true });
  };

  _getContents = (val) => {
    const contents = val.trim();

    this.setState({ contents: contents });
  };

  _getTitles = () => {
    const title = document.getElementsByName("title")[0].value.trim();

    this.setState({ title: title });
  };

  _getModifyData = async (board_id) => {
    const getData = await axios("/get/board_data", {
      method: "POST",
      headers: new Headers(),
      data: { id: board_id },
    });

    this.setState({
      title: getData.data[0].title,
      contents: getData.data[0].contents,
    });
  };

  render() {
    const { _changeState, _getContents, _getTitles, _getModifyData } = this;
    const { contents, title } = this.state;
    const {
      login,
      admin,
      user_ip,
      list_data,
      list_all_page,
      list_search,
      list_page,
      _changePage,
      _changeCatgory,
      user_id,
      _toggleModal,
      _getData,
      _getAllLike,
      data,
      date,
      like_num,
      pre_view,
      next_view,
      _getPreAndNextData,
      category_data,
      select_category,
      _selectCategoryData,
      like_exist,
      _getLikeExist,
      reply_data,
      reply_num,
      _getReplyData,
      reply_all_page,
      reply_page,
      reply_limit,
      reply_pre_block,
      reply_next_block,
      reply_block,
      reply_block_limit,
    } = this.props;

    return (
      <BrowserRouter>
        <div className="Mains">
          <div id="Mains-left">
            {/* 변경 전 <Route path='/' component={Category} exact/> */}
            <h5>
              <Link to="/admin/Dietboard/write">
                <Button variant="danger">식단 등록</Button>
              </Link>
            </h5>
            {/* <Link to="/realFinal/write">
            <Button variant="danger">식단 등록</Button>
          </Link> */}
            <Category
              _changeCatgory={_changeCatgory}
              login={login}
              _changeState={_changeState}
              admin={admin}
              user_ip={user_ip}
              exact
            />
          </div>

          <div>
            <Switch>
              <Route
                path="/admin/Dietboard/"
                component={this._withProps(List, {
                  category: this.state.category,
                  list_data: list_data,
                  list_all_page: list_all_page,
                  list_search: list_search,
                  list_page: list_page,
                  _changePage: _changePage,
                })}
                exact
              />

              <Route
                path="/admin/Dietboard/write/modify/:data"
                component={this._withProps(Write, {
                  _getContents: _getContents,
                  _getTitles: _getTitles,
                  contents: contents,
                  title: title,
                  _getModifyData: _getModifyData,
                })}
              />

              <Route
                path="/admin/Dietboard/write"
                component={this._withProps(Write, {
                  _getContents: _getContents,
                  _getTitles: _getTitles,
                  contents: contents,
                  title: title,
                })}
                exact
              />

              <Route
                path="/admin/Dietboard/view/:data"
                component={this._withProps(View, {
                  login: login,
                  user_id: user_id,
                  admin: admin,
                  _toggleModal: _toggleModal,
                  data: data,
                  date: date,
                  like_num: like_num,
                  _getData: _getData,
                  _getAllLike: _getAllLike,
                  pre_view: pre_view,
                  next_view: next_view,
                  _getPreAndNextData: _getPreAndNextData,
                  _getLikeExist: _getLikeExist,
                  like_exist: like_exist,
                  reply_data: reply_data,
                  reply_num: reply_num,
                  _getReplyData: _getReplyData,
                  _changePage: _changePage,
                  reply_all_page: reply_all_page,
                  reply_page: reply_page,
                  reply_limit: reply_limit,
                  reply_pre_block: reply_pre_block,
                  reply_next_block: reply_next_block,
                  reply_block: reply_block,
                  reply_block_limit: reply_block_limit,
                })}
              />
            </Switch>
          </div>

          <div id="Mains-right">
            <Switch>
              <Route
                path="/admin/Dietboard/write/modify/:data"
                component={this._withProps(Right_Write, {
                  contents: contents,
                  category: category_data,
                  select_category: select_category,
                  _selectCategoryData: _selectCategoryData,
                })}
              />

              <Route
                path="/admin/Dietboard/write"
                component={this._withProps(Right_Write, {
                  contents: contents,
                  category: category_data,
                  select_category: select_category,
                  _selectCategoryData: _selectCategoryData,
                })}
              />
            </Switch>
          </div>
        </div>
      </BrowserRouter>
    );
  }
}

export default main;
